import axios from 'axios';
import { useEffect, useState} from 'react';
import './movie.css'
import './chat.css'
import ChatBox from '../components/ChatBox';

const HOME = () => {

    const [data, setData] = useState([[]]);

    useEffect(() => {
        (async () => {
            const response = await axios.get(
              "http://localhost:4000/video"
            ); 

            setData(response.data.result);
        })();
        var movie = document.querySelector(".movie-box");
        console.log(movie);
      }, [data]);

    useEffect(() => {
        (async () => {
            const response = await axios.get(
              "http://localhost:4000/api/1.0/chat/spider-man?mId=1"
            ); 
            console.log(response.data.result);
            const interval = setTimeout(() => {
              setData(response.data.result);
            }, 100);
        })();
      }, [data]);  


    return(
        <>
            <div className="container d-flex justify-content-center">
                <video className='movie-box' controls muted>
                    <source src="http://localhost:4000/video" type="video/mp4"></source>
                </video>
                <ChatBox />
            </div>
        </>
    );


};

export default HOME;
