            if(favDialog!= null)
                favDialog.showModal();
        }        
    }
    function navi_party(){
        let r = generateString(9);
        api.getRoom(data.mId,r).then((json)=> {console.log(json);navigate(`/Party/${r}?room=${json.rId}`);})
    }

