import axios from 'axios';
import { useEffect, useState, useRef } from 'react';
import './chat.css'
import './video.scss'
import ChatBoxParty from '../components/ChatBoxParty';
import webSocket from 'socket.io-client'
import ProgressBar from '../components/ProgressBar';
import {useParams,useLocation} from "react-router-dom";

const Party = () => {
    var {mId} = useParams();
    console.log(mId);
    const useQuery = () => new URLSearchParams(useLocation().search);
    const query = useQuery();

    const refvideo = useRef();
    const [data, setData] = useState([[]]);
    const [ws,setWs] = useState(null)
    const [connect,setconnect] = useState(0);
    var socket;
    const connectWebSocket = () => {
        //開啟
         setWs(webSocket('http://localhost:4000'));
    }

    useEffect(() => {
      connectWebSocket();
      // progressLoop();
      console.log(mId);
    },[]);

    useEffect(()=>{
      if(ws){
          //連線成功在 console 中打印訊息
          console.log('success connect!')
          //設定監聽

          initWebSocket();

      }
    },[ws])

    function handleEvent(){
      var movie = document.querySelector(".movie-box");
      console.log("clickseek");
    }

    useEffect(() => {
        (async () => {
            const response = await axios.get(
              `http://localhost:4000/video`
            ); 
            setData(response.data.result);
        })();
        if(ws){
          var movie = document.querySelector(".movie-box");
          movie.addEventListener('canplaythrough', handleEvent);
        }  
      }, [ws]);

    // useEffect(() => {

    //         const response = await axios.get(
    //           "http://localhost:4000/api/1.0/chat/spider-man?mId=1"
    //         ); 
    //         const interval = setTimeout(() => {
    //           setData(response.data.result);
    //         }, 100);
    //   }, []);  

      const initWebSocket = () => {
        //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
        ws.on('getMessage', message => {
          console.log(message)
        })
        ws.on('getMessageid', (message) => {
          console.log(message);
          var movie = document.querySelector(".movie-box");
          movie.currentTime = message.time; 
          if(!movie.playing)
            movie.play();

        })

        ws.on('getMessageAll', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect){
              setconnect(movie.currentTime);
            }
            if(message.time){
              movie.currentTime = message.time;
            }
            if(message.playing=="play"){
              movie.play();
            }
            else if (message.playing=="pause"){
              movie.pause();
            }
        })

        ws.on('getMessageLess', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect){
              sendMessageid(message.connect,{time : movie.currentTime,playing : movie.paused});
            } 
        })
        ws.on('connect' , () => {
          sendMessageLess({connect : ws.id})
        });   
      }

    const sendMessage = (message) => {
      //以 emit 送訊息，並以 getMessageAll 為名稱送給 server 捕捉
      console.log(message);
      console.log(ws);
      ws.emit('getMessageAll', message);
    }

    const sendMessageLess = (message) => {
      //以 emit 送訊息，並以 getMessageLess 為名稱送給 server 捕捉
      console.log(message);
      ws.emit('getMessageLess', message);
    }

    const sendMessageid = (id,message) => {
      //以 emit 送訊息，並以 getMessageid 為名稱送給 server 捕捉

      ws.emit('getMessageid', id,message);
    }

    function handleseek(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.currentTime);
      sendMessage({time : movie.currentTime});
    }

    function handleclick(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.paused);
      console.log(ws.id);
      var message;
      if(movie.paused)
        message ={playing : "play"};
      else
        message = {playing : "pause"};  
      sendMessage(message);
    }

    // function progressLoop() {
    //   var movie = document.querySelector(".movie-box");
    //   setInterval(function () {
    //     document.getElementById("progress").value = Math.round(
    //       (movie.currentTime / movie.duration) * 100
    //     );
    //   });
    // }


    return(
        <>
            <div className="container d-flex justify-content-center">
            <figure>
            <video  width="1200" height="640" controls  ref={refvideo} id="video" className='movie-box' src={`http://localhost:4000/video${mId} `} onClick={handleclick}></video>
            <div class="video-container">
            <video class="video-container__video">
              <source src={`http://localhost:4000/video${mId}`} type="video/mp4"/>
            </video>
              <div class="video-container__controls"> 
                <div class="progress">
                  <div class="progress__current"></div>
                </div>
                <button class="control control--backward">
                  <i class="fas fa-backward"></i>
                </button>
                <button class="control control--play paused">
                  <i class="fas fa-play"></i>
                  <i class="fas fa-pause"></i>
                </button>
                <button class="control control--stop">
                  <i class="fas fa-stop"></i>
                </button>
                <button class="control control--forward">
                  <i class="fas fa-forward"></i>
                </button>
                <button class="control control--replay">
                  <i class="fas fa-sync"></i>
                </button>
                <button class="control control--volume">
                  <div class="control--volume__button">
                    <i class="fas fa-volume-off"></i>
                    <i class="fas fa-volume-up"></i>
                  </div>
                  <input class="control--volume__slider" value="1" type="range" min="0" max="1" step="0.01"/>
                </button>
                <button class="control control--fullscreen">
                  <i class="fas fa-expand"></i>
                  <i class="fas fa-compress"></i>
                </button>
              </div>
            </div>
            </figure>

                <ChatBoxParty mId={mId} rId={query.get('room')}/>
            </div>

        </>
    );


};

export default Party;
