import axios from 'axios';
import { useEffect, useState, useRef } from 'react';
import './movie.css'
import './chat.css'
import ChatBox from '../components/ChatBox';
import webSocket from 'socket.io-client'
import ProgressBar from '../components/ProgressBar';


const HOME = () => {
    const refvideo = useRef();
    const [data, setData] = useState([[]]);
    const [ws,setWs] = useState(null)
    const [connect,setconnect] = useState(0);
    var socket;
    const connectWebSocket = () => {
        //開啟
         setWs(webSocket('http://localhost:4000'));
    }

    useEffect(() => {
      connectWebSocket();
      progressLoop();
    },[]);

    useEffect(()=>{
      if(ws){
          //連線成功在 console 中打印訊息
          console.log('success connect!')
          //設定監聽

          initWebSocket();

      }
    },[ws])

    useEffect(() => {
        (async () => {
            const response = await axios.get(
              "http://localhost:4000/video"
            ); 
            setData(response.data.result);
        })();

      }, [ws]);

    // useEffect(() => {

    //         const response = await axios.get(
    //           "http://localhost:4000/api/1.0/chat/spider-man?mId=1"
    //         ); 
    //         const interval = setTimeout(() => {
    //           setData(response.data.result);
    //         }, 100);
    //   }, []);  

      const initWebSocket = () => {
        //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
        ws.on('getMessage', message => {
          console.log(message)
        })
        ws.on('getMessageid', (message) => {
          console.log(message);
          var movie = document.querySelector(".movie-box");
          movie.currentTime = message.time; 
          if(!movie.playing)
            movie.play();

        })

        ws.on('getMessageAll', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect){
              setconnect(movie.currentTime);
            }
            if(message.time){
              movie.currentTime = 60;
            }
            if(message.playing=="play"){
              movie.play();
            }
            else if (message.playing=="pause"){
              movie.pause();
            }
        })

        ws.on('getMessageLess', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect){
              sendMessageid(message.connect,{time : movie.currentTime,playing : movie.paused});
            } 
        })
        ws.on('connect' , () => {
          sendMessageLess({connect : ws.id})
        });   
      }

    const sendMessage = (message) => {
      //以 emit 送訊息，並以 getMessageAll 為名稱送給 server 捕捉
      console.log(message);
      console.log(ws);
      ws.emit('getMessageAll', message);
    }

    const sendMessageLess = (message) => {
      //以 emit 送訊息，並以 getMessageLess 為名稱送給 server 捕捉
      console.log(message);
      ws.emit('getMessageLess', message);
    }

    const sendMessageid = (id,message) => {
      //以 emit 送訊息，並以 getMessageid 為名稱送給 server 捕捉

      ws.emit('getMessageid', id,message);
    }

    function handleseek(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.paused);
      console.log(ws.id);
      var message;
      message ={time : movie.currentTime};
      sendMessage(message);
    }

    function handleclick(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.paused);
      console.log(ws.id);
      var message;
      if(movie.paused)
        message ={playing : "play"};
      else
        message = {playing : "pause"};  
      sendMessage(message);
    }

    function progressLoop() {
      var movie = document.querySelector(".movie-box");
      setInterval(function () {
        document.getElementById("progress").value = Math.round(
          (movie.currentTime / movie.duration) * 100
        );
      });
    }



    function playPause() { 
      var movie = document.querySelector(".movie-box");
      var button = document.getElementById( "play" );
      if ( movie.paused ) {
        movie.play();
        button.innerHTML = "❙❙";
      }
      else  {
        movie.pause(); 
        button.innerHTML = "►";
      }
    }

    button.addEventListener( "click", playPause );
    movie.addEventListener("play", progressLoop);

    return(
        <>
            <div className="container d-flex justify-content-center">
            <figure>
              <video  controls  ref={refvideo} id="video" className='movie-box' src="http://localhost:4000/video" onClick={handleclick}></video>
              <figcaption>
                <button id="play" aria-label="Play" role="button">►</button>
                <progress id="progress" max="100" value="0">Progress</progress>                
              </figcaption>
            </figure>

                <ChatBox />
            </div>

        </>
    );


};

export default HOME;
