import './insertMovie.css'
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import api from "../api/api"

function InsertMovie(props){
    let navigate = useNavigate(); 
    function dialog(){
        const favDialog = document.getElementById('favDialog');
        alert("hi");
        if(favDialog!= null)
            favDialog.showModal();
    }
    function navi_party(){

        api.getRoom(data.mId).then((json)=> {console.log(json);navigate(`/Party/${data.mId}?room=${json.rId}`);})
    }

    function navi_movie(){

        navigate(`/Movie/${data.mId}`);
    }

    function close(){
        const favDialog = document.getElementById('favDialog');
        if(favDialog!= null)
            favDialog.hidden = true;
    }
    const data = props.data;
    var image = (`../images/${data.image}`);
    console.log(image);
    return(

        <>
        <li>
            <div class="movie-card">
                <img className="main-img" src= {require("../images/spider-man.png")} onClick={() => dialog()} />
                <div>
                    <dialog id="favDialog">
                        <form method="dialog">
                            <button onClick={() => navi_movie()}>watch Online</button>
                            <button onClick={() => navi_party()}>watch with friends</button>
                            <button  value="cancel">Close</button>
                        </form>
                    </dialog>
                    <output></output>

                </div>

            <div class="title-wrapper">
                    <a href="./movie-details.html">

                        <h3 class="card-title">{data[0].title}</h3>
                    </a>

                    <time datetime="2022">2022</time>
                    </div>

                    <div class="card-meta">
                    <div class="badge badge-outline">HD</div>

                    <div class="duration">
                        <ion-icon name="time-outline"></ion-icon>

                        <time datetime="PT137M">137 min</time>
                    </div>

                    <div class="rating">
                        <ion-icon name="star"></ion-icon>

                        <data>8.5</data>
                    </div>
                    </div>

            </div>
            </li>
        </>

    )

};

export default InsertMovie;
