import axios from 'axios';
import { useEffect, useState} from 'react';
import './movie.css'
import './chat.css'
import ChatBox from '../components/ChatBox';
import webSocket from 'socket.io-client'


const HOME = () => {

    const [data, setData] = useState([[]]);
    const [ws,setWs] = useState(null)
    const [connect,setconnect] = useState(0);

    const connectWebSocket = () => {
        //開啟
        setWs(webSocket('http://localhost:4000'));
    }

    useEffect(() => {
      connectWebSocket();
      // sendMessageLess({connect : "new"})

    },[]);

    useEffect(()=>{
      if(ws){
          //連線成功在 console 中打印訊息
          console.log('success connect!')
          //設定監聽
          initWebSocket()
      }
    },[ws])

    useEffect(() => {
        (async () => {
            const response = await axios.get(
              "http://localhost:4000/video"
            ); 
            setData(response.data.result);
        })();

      }, [data]);

    useEffect(() => {
            var movie = document.querySelector(".movie-box");
            console.log(movie);
            movie.addEventListener("seeking", handleseek);
    //         const response = await axios.get(
    //           "http://localhost:4000/api/1.0/chat/spider-man?mId=1"
    //         ); 
    //         const interval = setTimeout(() => {
    //           setData(response.data.result);
    //         }, 100);
      }, []);  

      const initWebSocket = () => {
        //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
        ws.on('getMessage', message => {
          console.log(message)
        })
        ws.on('getMessageAll', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect){
              setconnect(movie.currentTime);
            }
            if(message.time){
              movie.currentTime = message.time
            }
            if(message.playing=="play"){
              movie.play();
            }
            else if (message.playing=="pause"){
              movie.pause();
            }
        })
        ws.on('getMessageLess', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect=="new"){
              sendMessage("");
            }
        })
      }

      const sendMessage = (message) => {
          //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
          console.log(message);
          ws.emit('getMessageAll', message);
      }

      const sendMessageLess = (message) => {
        //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
        console.log(message);
        ws.emit('getMessageLess', message);
    }


    function handleseek(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.currentTime);
      sendMessage({time : movie.currentTime});
    }

    function handleclick(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.paused);
      console.log(ws);
      var message;
      if(movie.paused)
        message ={playing : "play"};
      else
        message = {playing : "pause"};  
      sendMessage(message);
    }


    return(
        <>
            <div className="container d-flex justify-content-center">
                <video className='movie-box' controls muted onClick={handleclick}>
                    <progress onClick={handleclick} id='p' min="0" max="100" value="0"></progress>
                    <source src="http://localhost:4000/video" type="video/mp4"></source>
                    <div class="progress">
                        <progress id="progress" value="0" min="0">
                          <span id="progress-bar"></span>
                        </progress>
                    </div>
                </video>

                <ChatBox />
            </div>

        </>
    );


};

export default HOME;
