import axios from 'axios';
import { useEffect, useState} from 'react';

const ChatBox = () => {

    const [data, setData] = useState();

    useEffect(() => {
        (async () => {
            const response = await axios.get(
              "http://localhost:4000/api/1.0/chat/spider-man?mId=1"
            ); 
            console.log(response.data.result);

            setData(response.data.result);
        })();
      }, []);


      function renderChatcontent() {
        console.log(data);  
        return (
            <div className="product__size-selector">
            {data.content.map((Content) => {
              return (
                <div class="d-flex flex-row p-3">
                <img src="https://img.icons8.com/color/48/000000/circled-user-female-skin-type-7.png" width="30" height="30"/>
                <div class="chat ml-2 p-3"><span class="text-muted dot">{Content}</span></div>
                </div>
              );
            })}
          </div>
        );
      } 

    if(!data)
      return null;  
    return(
        <>
            <div className="container d-flex justify-content-center">
                <div class="card mt-5">
                    <div class="d-flex flex-row justify-content-between p-3 adiv text-white">
                        <i class="fas fa-chevron-left"></i>
                        <span class="pb-3">Live chat</span>
                        <i class="fas fa-times"></i>
                    </div>
                    {renderChatcontent()}

                    <div class="form-group px-3">
                        <textarea class="form-control" rows="5" placeholder="Type your message"></textarea>
                    </div>
                </div>
            </div>
        </>
    );


};

export default ChatBox;
