import axios from 'axios';
import { useEffect, useState, useRef } from 'react';
import './chat.css'
import ChatBoxMovie from '../components/ChatBoxMovie';
import webSocket from 'socket.io-client'
import ProgressBar from '../components/ProgressBar';
import {useParams,useLocation} from "react-router-dom";
import Party from './Party';

const Room = () => {


    const useQuery = () => new URLSearchParams(useLocation().search);
    const code = useParams();
    const query = useQuery();
    const [mId,setmId] = useState();
    useEffect(() => {
        console.log(code.mId);
        (async () => {
            const response = await axios.get(
              `http://localhost:4000/api/1.0/chat/room/${code.mId}?room=${query.get('room')}`
            ); 
            console.log(response.data.result);
            setmId(response.data.result[0].mId);
        })();

      }, [mId]);


     if(!mId) return null;

    return(
        <div>
            <Party mId={mId}/>
            <div class="container my-card">
                <div class="row">
                    <div class="col-md-6 col-xl-4">                       
                    <div class="card">
                        <div class="card-body">
                        <div class="media align-items-center"><span style="" class="avatar avatar-xl mr-3"></span>
                            <div class="media-body overflow-hidden">
                            <h5 class="card-text mb-0">Nielsen Cobb</h5>
                            <p class="card-text text-uppercase text-muted">Memora</p>
                            <p class="card-text">

                                nielsencobb@memora.com<br/><abbr title="Phone">P:  </abbr>+1 (851) 552-2735
                            </p>
                            </div>
                        </div><a href="#" class="tile-link"></a>
                        </div>
                    </div>
                    </div>

                </div>
            </div>   
        </div>    
    );


};

export default Room;
