import axios from "axios";

export default {
    getMovies: async () => {
        return  fetch('http://localhost:4000/api/1.0/movie/get')
        .then(res => 
            res.json())
    },
    getRoom: async (mId) => {
        return  fetch('http://localhost:4000/api/1.0/party/createroom', {
            method: 'POST',
            body: JSON.stringify({"mId":mId})
            })
        .then(res => 
            res.json())
    },

}
