import axios from 'axios';
import { useEffect, useState, useRef } from 'react';
import './chat.css'
import './video.css'
import ChatBoxParty from '../components/ChatBoxParty';
import webSocket from 'socket.io-client'
import ProgressBar from '../components/ProgressBar';
import {useParams,useLocation} from "react-router-dom";

const Party = () => {
    var {mId} = useParams();
    console.log(mId);
    const useQuery = () => new URLSearchParams(useLocation().search);
    const query = useQuery();

    const refvideo = useRef();
    const [data, setData] = useState([[]]);
    const [ws,setWs] = useState(null)
    const [connect,setconnect] = useState(0);
    var socket;
    const connectWebSocket = () => {
        //開啟
         setWs(webSocket('http://localhost:4000'));
    }

    useEffect(() => {
      connectWebSocket();
      // progressLoop();
      console.log(mId);
    },[]);

    useEffect(()=>{
      if(ws){
          //連線成功在 console 中打印訊息
          console.log('success connect!')
          //設定監聽

          initWebSocket();

      }
    },[ws])

    function handleEvent(){
      var movie = document.querySelector(".movie-box");
      console.log("clickseek");
    }

    useEffect(() => {
        (async () => {
            const response = await axios.get(
              `http://localhost:4000/video`
            ); 
            setData(response.data.result);
        })();
        if(ws){
          var movie = document.querySelector(".movie-box");
          movie.addEventListener('canplaythrough', handleEvent);
        }  
      }, [ws]);

    // useEffect(() => {

    //         const response = await axios.get(
    //           "http://localhost:4000/api/1.0/chat/spider-man?mId=1"
    //         ); 
    //         const interval = setTimeout(() => {
    //           setData(response.data.result);
    //         }, 100);
    //   }, []);  

      const initWebSocket = () => {
        //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
        ws.on('getMessage', message => {
          console.log(message)
        })
        ws.on('getMessageid', (message) => {
          console.log(message);
          var movie = document.querySelector(".movie-box");
          movie.currentTime = message.time; 
          if(!movie.playing)
            movie.play();

        })

        ws.on('getMessageAll', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect){
              setconnect(movie.currentTime);
            }
            if(message.time){
              movie.currentTime = message.time;
            }
            if(message.playing=="play"){
              movie.play();
            }
            else if (message.playing=="pause"){
              movie.pause();
            }
        })

        ws.on('getMessageLess', message => {
            console.log(message);
            var movie = document.querySelector(".movie-box");
            if(message.connect){
              sendMessageid(message.connect,{time : movie.currentTime,playing : movie.paused});
            } 
        })
        ws.on('connect' , () => {
          sendMessageLess({connect : ws.id})
        });   
      }

    const sendMessage = (message) => {
      //以 emit 送訊息，並以 getMessageAll 為名稱送給 server 捕捉
      console.log(message);
      console.log(ws);
      ws.emit('getMessageAll', message);
    }

    const sendMessageLess = (message) => {
      //以 emit 送訊息，並以 getMessageLess 為名稱送給 server 捕捉
      console.log(message);
      ws.emit('getMessageLess', message);
    }

    const sendMessageid = (id,message) => {
      //以 emit 送訊息，並以 getMessageid 為名稱送給 server 捕捉

      ws.emit('getMessageid', id,message);
    }

    function handleseek(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.currentTime);
      sendMessage({time : movie.currentTime});
    }

    function handleclick(event){
      event.preventDefault();
      var movie = document.querySelector(".movie-box");
      console.log(movie.paused);
      console.log(ws.id);
      var message;
      if(movie.paused)
        message ={playing : "play"};
      else
        message = {playing : "pause"};  
      sendMessage(message);
    }

    // function progressLoop() {
    //   var movie = document.querySelector(".movie-box");
    //   setInterval(function () {
    //     document.getElementById("progress").value = Math.round(
    //       (movie.currentTime / movie.duration) * 100
    //     );
    //   });
    // }


    return(
        <>
            <div className="container d-flex justify-content-center">
            <figure>
              <div class='player-container'>
                <div class='player'>
                <video  width="1200" height="640" autoplay playsinline  ref={refvideo} id="video" className='movie-box' src={`http://localhost:4000/video${mId} `} onClick={handleclick}></video>  
                  <div class='play-btn-big'></div>
                  <div class='controls'>
                    <div class="time"><span class="time-current"></span><span class="time-total"></span></div>
                    <div class='progress'>
                      <div class='progress-filled'></div>
                    </div>
                  <div class='controls-main'>
                    <div class='controls-left'>
                      <div class='volume'>
                        <div class='volume-btn loud'>
                          <svg width="26" height="24" viewBox="0 0 26 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M6.75497 17.6928H2C0.89543 17.6928 0 16.7973 0 15.6928V8.30611C0 7.20152 0.895431 6.30611 2 6.30611H6.75504L13.9555 0.237289C14.6058 -0.310807 15.6 0.151473 15.6 1.00191V22.997C15.6 23.8475 14.6058 24.3098 13.9555 23.7617L6.75497 17.6928Z" transform="translate(0 0.000518799)" fill="white"/>
                            <path id="volume-low" d="M0 9.87787C2.87188 9.87787 5.2 7.66663 5.2 4.93893C5.2 2.21124 2.87188 0 0 0V2C1.86563 2 3.2 3.41162 3.2 4.93893C3.2 6.46625 1.86563 7.87787 0 7.87787V9.87787Z" transform="translate(17.3333 7.44955)" fill="white"/>

                            <path id="volume-high" d="M0 16.4631C4.78647 16.4631 8.66667 12.7777 8.66667 8.23157C8.66667 3.68539 4.78647 0 0 0V2C3.78022 2 6.66667 4.88577 6.66667 8.23157C6.66667 11.5773 3.78022 14.4631 0 14.4631V16.4631Z" transform="translate(17.3333 4.15689)" fill="white"/> 
                            <path id="volume-off" d="M1.22565 0L0 1.16412L3.06413 4.0744L0 6.98471L1.22565 8.14883L4.28978 5.23853L7.35391 8.14883L8.57956 6.98471L5.51544 4.0744L8.57956 1.16412L7.35391 0L4.28978 2.91031L1.22565 0Z" transform="translate(17.3769 8.31403)" fill="white"/>
                          </svg>

                        </div>
                      <div class='volume-slider'>
                      <div class='volume-filled'></div>
                    </div>
                  </div>
                  </div>
                    <div class='play-btn paused'></div>
                    <div class="controls-right">
                      <div class='speed'>
                          <ul class='speed-list'>
                            <li class='speed-item' data-speed='0.5'>0.5x</li>
                            <li class='speed-item' data-speed='0.75'>0.75x</li>
                            <li class='speed-item active' data-speed='1'>1x</li>
                            <li class='speed-item' data-speed='1.5'>1.5x</li>
                            <li class='speed-item' data-speed='2'>2x</li>
                          </ul>
                      </div>
